import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3002');

function App() {
  const [username, setUsername] = useState('');
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [activeUsers, setActiveUsers] = useState([]);
  const [userHistory, setUserHistory] = useState([]);
  const messageListRef = useRef(null);
  const inputContainerRef = useRef(null);
  const [chatStarted, setChatStarted] = useState(false);
  const [loadingMessages, setLoadingMessages] = useState(false);

  const handleStartChat = () => {
    if (username) {
      setChatStarted(true);
      socket.connect();
      socket.emit('join', username);
    }
  };

  useEffect(() => {
    if (chatStarted) {
      socket.on('connect', () => {
        console.log('Connected to server');
      });

      socket.on('chat message', (message) => {
        setMessages((prevMessages) => [...prevMessages, message]);
        scrollToBottom();
      });

      socket.on('active users', (users) => {
        setActiveUsers(users);
      });

      socket.on('user history', (history) => {
        setUserHistory(history);
      });

      socket.on('disconnect', () => {
        console.log('Disconnected from server');
        setActiveUsers([]);
      });

      return () => {
        socket.disconnect();
      };
    }
  }, [chatStarted]);

  useEffect(() => {
    const fetchMessages = async () => {
      setLoadingMessages(true);
      try {
        const response = await fetch('http://localhost:3002/messages');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setMessages(data);
        scrollToBottom();
      } catch (error) {
        console.error('Fetching messages failed: ', error);
      } finally {
        setLoadingMessages(false);
      }
    };

    if (chatStarted) {
      fetchMessages();
    }
  }, [chatStarted]);

  const scrollToBottom = () => {
    if (messageListRef.current) {
      messageListRef.current.scrollTop = messageListRef.current.scrollHeight;
    }
  };

  const handleSendMessage = () => {
    if (inputMessage) {
      const message = {
        username,
        message: inputMessage,
      };

      socket.emit('chat message', message);

      setInputMessage('');
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
      scrollToBottom();
    }
  };

  useEffect(() => {
    const updateActiveUsers = () => {
      const activeUsernames = userHistory
        .filter((user) => user.status === 'connected')
        .map((user) => user.username);
      setActiveUsers(activeUsernames);
    };

    updateActiveUsers();
  }, [userHistory]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      {!chatStarted ? (
        <div className="flex flex-col items-center">
          <h1 className="text-2xl font-bold mb-4">Enter your username</h1>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Your username..."
            className="px-4 py-2 rounded-md border border-gray-300 mb-4"
          />
          <button
            onClick={handleStartChat}
            className="px-4 py-2 rounded-md bg-blue-500 text-white"
          >
            Start Chat
          </button>
        </div>
      ) : (
        <div className="flex w-full h-screen">
          <div className="flex flex-col w-1/4 p-4 bg-white border-r border-gray-300">
            <h2 className="text-lg font-bold mb-4">Active Users</h2>
            {activeUsers.length > 0 ? (
              <ul className="space-y-2">
                {activeUsers.map((user) => (
                  <li key={user} className="text-gray-600">{user}</li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-600">No active users</p>
            )}
          </div>
          <div className="flex flex-col flex-1 p-4">
            <div
              className="flex-1 overflow-y-auto px-4 py-2 bg-white mb-4"
              ref={messageListRef}
            >
              {loadingMessages ? (
                <p className="text-gray-600">Loading messages...</p>
              ) : messages.length > 0 ? (
                messages.map((message, index) => (
                  <div key={index} className="mb-2">
                    <span className="font-bold">{message.username}: </span>
                    <span>{message.message}</span>
                  </div>
                ))
              ) : (
                <p className="text-gray-600">No messages</p>
              )}
            </div>
            <div className="flex items-center">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="Type your message..."
                className="flex-1 px-4 py-2 rounded-md border border-gray-300 mr-4"
              />
              <button
                onClick={handleSendMessage}
                className="px-4 py-2 rounded-md bg-blue-500 text-white"
              >
                Send
              </button>
            </div>
          </div>
          <div className="flex flex-col w-1/4 p-4 bg-white border-l border-gray-300">
            <h2 className="text-lg font-bold mb-4">User History</h2>
            {userHistory.length > 0 ? (
              <ul className="space-y-2">
                {userHistory.map((user, index) => (
                  <li key={index} className="text-gray-600">
                    {user.username} - {user.status}
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-600">No user history</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
